            <Canvas HorizontalAlignment="Left">
                <Rectangle Height="106" Stroke="#FF1B0F0F" Width="135" UseLayoutRounding="True" RadiusX="5" RadiusY="5" Canvas.Left="11" Canvas.Top="20" HorizontalAlignment="Center" VerticalAlignment="Top"/>
