                <Label x:Name="Label2022" Content="SERVER 2022" FontWeight="Bold" Background="#FF1B0F0F" Foreground="White" Padding="8,4,8,4" Canvas.Left="156" HorizontalAlignment="Left" Canvas.Top="8" VerticalAlignment="Center"/>
                <RadioButton VerticalContentAlignment="Center" x:Name="radioButton2022Standard" Content="Standard " Canvas.Top="39" Canvas.Left="168" HorizontalAlignment="Left" VerticalAlignment="Center"/>
