                <RadioButton x:Name="radioButton11ProfessionalWorkstationN" Content="Pro for Workstation N" VerticalContentAlignment="Center" IsChecked="False" Padding="5,5,5,5" Canvas.Top="275" Canvas.Left="844" HorizontalAlignment="Left" VerticalAlignment="Center"/>
                <TextBox x:Name="textBox1" TextWrapping="Wrap" Text="(1) Select the destination edition you want to convert to." Canvas.Top="286" FontSize="10.5" BorderBrush="{x:Null}" Background="{x:Null}" HorizontalAlignment="Left" VerticalAlignment="Top" Canvas.Left="6" Padding="0,0,0,2" TextChanged="textBox1_TextChanged"/>
                <TextBox x:Name="textBox2" TextWrapping="Wrap" Text="(2) Click on the Submit button and wait for a while." Canvas.Top="310" FontSize="10.5" BorderBrush="{x:Null}" Background="{x:Null}" HorizontalAlignment="Left" VerticalAlignment="Top" Canvas.Left="6" Padding="0,0,0,2"/>
                <TextBox x:Name="textBox3" TextWrapping="Wrap" Text="(*) The tool supports convert all Windows editions." Canvas.Top="331" FontSize="10.5" BorderBrush="{x:Null}" Background="{x:Null}" HorizontalAlignment="Left" VerticalAlignment="Center" Canvas.Left="6" Padding="0,0,0,2"/>
                <TextBox x:Name="textBox4" TextWrapping="Wrap" Text="(*) Auto activate the Windows license after converting." Canvas.Top="352" FontSize="10.5" BorderBrush="{x:Null}" Background="{x:Null}" HorizontalAlignment="Left" VerticalAlignment="Top" Canvas.Left="6" Padding="0,0,0,2"/>
                <TextBox x:Name="textBox5" TextWrapping="Wrap" Text="(*) More FREE Microsoft products, please visit:" Canvas.Top="373" FontSize="10.5" BorderBrush="{x:Null}" Background="{x:Null}" Canvas.Left="6" HorizontalAlignment="Left" VerticalAlignment="Top" Padding="0,0,0,2"/>
